// Tipos adaptados para as novas fileiras
type CategoriaId = 'todos' | 'novidades' | 'vestidos' | 'blusas' | 'calcas' | 'acessorios' | 'conjuntos';

type AuthView = 'login' | 'cadastro';

interface Produto {
  id: number;
  nome: string;
  preco: number;
  categoria: Exclude<CategoriaId, 'todos' | 'novidades'>;
  imagens: string[];
  imagem: string;
  novo: boolean;
  tamanhos: string[];
  descricao: string;
}

type CarrinhoItem = Produto & {
  qtd: number;
  tamanho: string;
  imagemSelecionada: string;
};

type ChatMessage = {
  tipo: 'bot' | 'user';
  texto: string;
};

interface Usuario {
  nome: string;
  email: string;
}

interface AuthFormState {
  nome: string;
  email: string;
  senha: string;
}

